import 'leaflet/dist/leaflet.css';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet';
import 'leaflet-draw';
import 'leaflet-polylinedecorator/dist/leaflet.polylineDecorator.js';
import '../LeafletMaps/Map2.css';
import React, { useEffect } from 'react';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';

const copyBoundingBoxCoordinates = () => {
  const bboxCoordinatesElement = document.getElementById('bbox-coordinates');
  const bboxCoordinates = bboxCoordinatesElement.textContent;

  navigator.clipboard.writeText(bboxCoordinates)
    .then(() => {
      alert('Koordinat berhasil disalin!');
    })
    .catch((error) => {
      console.error('Error:', error);
    });
};

const MapComponent2 = () => {
  useEffect(() => {
    const map = L.map('map').setView([0, 0], 2);
    const mapBounds = L.latLngBounds();

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; OpenStreetMap contributors',
      maxZoom: 18,
    }).addTo(map);

    const aircraftLayer = L.layerGroup().addTo(map);
    const aircraftMovementLayer = L.layerGroup().addTo(map);

    const drawnItems = new L.FeatureGroup();
    map.addLayer(drawnItems);
    const drawControl = new L.Control.Draw({
      draw: {
        rectangle: true,
        polyline: false,
        circle: false,
        circlemarker: false,
        marker: false,
        polygon: false
      },
      edit: {
        featureGroup: drawnItems,
        remove: true,
      },
    });
    map.addControl(drawControl);

    const showBoundingBoxCoordinates = (bounds) => {
      const roundedBounds = bounds.toBBoxString();

      const bboxCoordinatesElement = document.getElementById('bbox-coordinates');
      bboxCoordinatesElement.innerHTML = `${bounds.toBBoxString()}`;

      const layer = L.rectangle(bounds);
      layer.bindPopup(`BBox: ${bounds.toBBoxString()}`).openPopup();
      drawnItems.addLayer(layer);

      updateAircraftPositions(bounds);

      mapBounds.extend(bounds);
    };

    const drawCreatedHandler = (event) => {
      drawnItems.clearLayers();

      const layer = event.layer;
      drawnItems.addLayer(layer);
      const bounds = layer.getBounds();
      showBoundingBoxCoordinates(bounds);
      map.fitBounds(bounds);
    };

    map.on('draw:created', drawCreatedHandler);

    const drawDeletedHandler = (event) => {
      const layers = event.layers;
      layers.eachLayer((layer) => {
        drawnItems.removeLayer(layer);
        aircraftMovementLayer.clearLayers();
      });

      const bboxCoordinatesElement = document.getElementById('bbox-coordinates');
      bboxCoordinatesElement.innerHTML = "";
    };

    map.on('draw:deleted', drawDeletedHandler);

    return () => {
      map.off('draw:created', drawCreatedHandler);
      map.off('draw:deleted', drawDeletedHandler);
    };
  }, []);

  return (
    <div>
      <div id="map" className="map2"></div>

      <div className="bbox-result">
        <strong>Hasil Koordinat Bbox:</strong>
        <span id="bbox-coordinates"></span><br />
        <button id="copy-button" onClick={copyBoundingBoxCoordinates}>
          Salin
        </button>
      </div>
    </div>
  );
};

export default MapComponent2;
