import React, { Component } from 'react';
import 'ol/ol.css';
import './Map.css';
import Map from 'ol/Map.js';
import View from 'ol/View.js';
import { Stroke, Style, Icon, Fill } from 'ol/style.js';
import { Tile as TileLayer, Vector as VectorLayer } from 'ol/layer.js';
import OSM from 'ol/source/OSM.js';
import VectorSource from 'ol/source/Vector.js';
import Feature from 'ol/Feature.js';
import { fromLonLat } from 'ol/proj.js';
import LineString from 'ol/geom/LineString.js';
import { getVectorContext } from 'ol/render.js';
import { getWidth } from 'ol/extent.js';
import arc from 'arc';
import airplaneimg from './614.png';

class MapComponent extends Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
    this.map = null;
    this.flightsSource = null;
    this.tileLayer = null;
    this.flightsLayer = null;
    this.style = null;
    this.airplaneIcon = null;
    this.pointsPerMs = 0.02;
    this.state = {
      coordinateInput: '',
      lamin: null,
      lomin: null,
      lamax: null,
      lomax: null,
      bboxLayer: null, // Tambahkan bboxLayer ke state
    };
  }

  componentDidMount() {
    this.initializeMap();
  }

  initializeMap() {
    this.tileLayer = new TileLayer({
      source: new OSM(),
    });

    this.map = new Map({
      layers: [this.tileLayer],
      target: this.mapRef.current,
      view: new View({
        center: [-11000000, 4600000],
        zoom: 2,
      }),
    });

    this.airplaneIcon = new Icon({
      src: airplaneimg,
      scale: 0.5,
    });

    this.style = new Style({
      stroke: new Stroke({
        color: 'red',
        width: 2,
      }),
      image: this.airplaneIcon,
    });

    this.flightsSource = new VectorSource({
      loader: () => {
        const url = '/data/openflights/flights.json';
        fetch(url)
          .then((response) => {
            if (!response.ok) {
              throw new Error('Network response was not ok');
            }
            return response.json();
          })
          .then((json) => {
            const flightsData = json.flights;
            for (let i = 0; i < flightsData.length; i++) {
              const flight = flightsData[i];
              const from = flight[0];
              const to = flight[1];

              const arcGenerator = new arc.GreatCircle(
                { x: from[1], y: from[0] },
                { x: to[1], y: to[0] }
              );

              const arcLine = arcGenerator.Arc(100, { offset: 10 });
              const features = [];
              arcLine.geometries.forEach((geometry) => {
                const line = new LineString(geometry.coords);
                line.transform('EPSG:4326', 'EPSG:3857');

                features.push(
                  new Feature({
                    geometry: line,
                    finished: false,
                  })
                );
              });

              this.addLater(features, i * 50);
            }

            this.tileLayer.on('postrender', this.animateFlights);
          })
          .catch((error) => {
            console.error('Error fetching JSON:', error);
          });
      },
    });

    this.flightsLayer = new VectorLayer({
      source: this.flightsSource,
      style: (feature) => {
        if (feature.get('finished')) {
          return null;
        }
        this.style.getImage().setRotation(
          -Math.atan2(
            feature.getGeometry().getCoordinates()[0][3] -
              feature.getGeometry().getCoordinates()[0][0],
            feature.getGeometry().getCoordinates()[0][2] -
              feature.getGeometry().getCoordinates()[0][1]
          ) +
            Math.PI / 2
        );
        return this.style;
      },
    });

    this.map.addLayer(this.flightsLayer);

    this.drawBoundingBox();
    this.map.on('movestart', this.handleMapMove); // Tambahkan event listener untuk movestart
  }

  addLater(features, timeout) {
    window.setTimeout(() => {
      this.flightsSource.addFeatures(features);
    }, timeout);
  }

  animateFlights = (event) => {
    const vectorContext = getVectorContext(event);
    const frameState = event.frameState;
    const elapsed = frameState.time - this.now;
    this.now = frameState.time;
    const features = this.flightsSource.getFeatures();
    for (let i = 0; i < features.length; i++) {
      const feature = features[i];
      const coords = feature.getGeometry().getCoordinates();
      const move = (this.pointsPerMs * elapsed) / 1000;
      const index = Math.round((this.M * move) / this.resolution);
      if (index >= coords.length) {
        feature.set('finished', true);
      } else {
        const currentPoint = new Point(coords[index]);
        const featureClone = new Feature(currentPoint);
        vectorContext.drawFeature(featureClone, this.style);
      }
    }
    this.map.render();
    this.map.on('postcompose', this.animateFlights);
  };

  drawBoundingBox() {
    const bboxSource = new VectorSource();
    const bboxFeature = new Feature({
      geometry: new LineString([
        fromLonLat([this.state.lomin, this.state.lamin]),
        fromLonLat([this.state.lomax, this.state.lamin]),
        fromLonLat([this.state.lomax, this.state.lamax]),
        fromLonLat([this.state.lomin, this.state.lamax]),
        fromLonLat([this.state.lomin, this.state.lamin]),
      ]),
    });
    bboxSource.addFeature(bboxFeature);

    const bboxLayer = new VectorLayer({
      source: bboxSource,
      style: new Style({
        stroke: new Stroke({
          color: 'blue',
          width: 5,
        }),
        fill: new Fill({
          color: 'rgba(0, 0, 255, 0.1)',
        }),
      }),
    });

    this.setState({ bboxLayer }); // Atur properti bboxLayer di state

    this.map.addLayer(bboxLayer);
  }

  handleMapMove = () => {
    const { bboxLayer } = this.state; // Dapatkan bboxLayer dari state

    if (bboxLayer && this.map.getLayers().getArray().includes(bboxLayer)) {
      const viewExtent = this.map.getView().calculateExtent(this.map.getSize());
      const bboxExtent = bboxLayer.getSource().getExtent();
      const isInsideBbox = ol.extent.containsExtent(bboxExtent, viewExtent);

      if (!isInsideBbox) {
        this.stopFlightsAnimation();
      }
    }
  };

  stopFlightsAnimation() {
    const features = this.flightsSource.getFeatures();
    features.forEach((feature) => {
      feature.set('finished', true);
    });
    this.tileLayer.un('postrender', this.animateFlights);
    this.map.render();
  }

  handleCoordinateInputChange = (event) => {
    this.setState({
      coordinateInput: event.target.value,
    });
  };

  handleBoundingBoxSubmit = (event) => {
    event.preventDefault();

    const coordinates = this.state.coordinateInput.split(',');
    if (coordinates.length !== 4) {
      alert('Invalid coordinate format. Please enter coordinates in the format "lat_min,lon_min,lat_max,lon_max".');
      return;
    }

    const [lamin, lomin, lamax, lomax] = coordinates.map((coord) => parseFloat(coord));

    if (isNaN(lamin) || isNaN(lomin) || isNaN(lamax) || isNaN(lomax)) {
      alert('Invalid coordinate values. Please enter valid numeric values for coordinates.');
      return;
    }

    if (lamin >= lamax || lomin >= lomax) {
      alert('Invalid coordinate values. Please ensure that min values are less than max values.');
      return;
    }

    this.setState(
      {
        lamin,
        lomin,
        lamax,
        lomax,
      },
      () => {
        this.drawBoundingBox();
      }
    );
  };

  render() {
    return (
      <div>
        <div id="map" className="map" ref={this.mapRef}></div>
        <form onSubmit={this.handleBoundingBoxSubmit}>
          <label htmlFor="coordinates">Enter Bounding Box Coordinates (lat_min, lon_min, lat_max, lon_max):</label>
          <input
            type="text"
            id="coordinates"
            value={this.state.coordinateInput}
            onChange={this.handleCoordinateInputChange}
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}

export default MapComponent;
