import React, { Component } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet-draw';

class MapComponent2 extends Component {
  constructor(props) {
    super(props);

    this.map = null;
    this.mapRef = React.createRef();
    this.drawnItems = null;

    this.state = {
      coordinates: [],
    };
  }

  componentDidMount() {
    this.map = L.map(this.mapRef.current).setView([51.505, -0.09], 5);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data © <a href="https://openstreetmap.org">OpenStreetMap</a> contributors',
    }).addTo(this.map);

    this.drawnItems = new L.FeatureGroup();
    this.map.addLayer(this.drawnItems);

    const drawControl = new L.Control.Draw({
      draw: {
        rectangle: true,
        polyline: false,
        circle: false,
        circlemarker: false,
        marker: false,
        polygon: false
      },
      edit: {
        featureGroup: this.drawnItems
      }
    });

    this.map.addControl(drawControl);

    this.map.on(L.Draw.Event.CREATED, (event) => {
      const { layer } = event;

      // Ambil koordinat hasil dari objek gambar
      const type = event.layerType;
      const latLngs = layer._latlngs;

      let coordinates = [];
      if (type === 'rectangle') {
        const bounds = latLngs[0];
        coordinates = [
          [bounds[0].lat, bounds[0].lng],
          [bounds[2].lat, bounds[2].lng]
        ];
      }

      // Simpan koordinat hasil ke dalam state
      this.setState({ coordinates: coordinates });

      // Tambahkan layer ke drawnItems agar dapat ditampilkan pada peta
      this.drawnItems.addLayer(layer);
    });
  }

  componentWillUnmount() {
    this.map.remove();
    this.map = null;
  }

  render() {
    const { coordinates } = this.state;

    return (
      <div>
        <div ref={this.mapRef} style={{ height: '760px' }} />
        {coordinates.length > 0 && (
          <div>
            <h3>Hasil Koordinat:</h3>
            <ul>
              {coordinates.map((coord, index) => (
                <li key={index}>{`[${coord[0]}, ${coord[1]}]`}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    );
  }
}

export default MapComponent2;
