import React, { Component } from 'react';
import 'ol/ol.css';
import './Map.css';
import { Map, View } from 'ol';
import { Tile as TileLayer, Vector as VectorLayer } from 'ol/layer';
import { OSM, Vector as VectorSource } from 'ol/source';
import { Fill, Stroke, Style } from 'ol/style';
import { getVectorContext } from 'ol/render';
import { LineString } from 'ol/geom';
import arc from 'arc.js'; // Import the 'arc' module
import Feature from 'ol/Feature'; // Import the 'Feature' class

class MapComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      center: [0, 0],
      zoom: 1,
    };

    this.map = null;
    this.mapRef = React.createRef();
    this.flightsSource = new VectorSource();
    this.flightsLayer = new VectorLayer({
      source: this.flightsSource,
      style: new Style({
        stroke: new Stroke({
          color: '#EAE911',
          width: 2,
        }),
      }),
    });
  }

  componentDidMount() {
    this.map = new Map({
      target: this.mapRef.current,
      layers: [
        new TileLayer({
          source: new OSM(),
        }),
        this.flightsLayer,
      ],
      view: new View({
        center: this.state.center,
        zoom: this.state.zoom,
      }),
    });

    this.map.on('moveend', () => {
      const center = this.map.getView().getCenter();
      const zoom = this.map.getView().getZoom();
      this.setState({ center, zoom });
    });

    this.loadFlights();
  }

  loadFlights = () => {
    const url = 'data/openflights/flights.json';
    fetch(url)
      .then((response) => response.json())
      .then((json) => {
        const flightsData = json.flights;
        for (let i = 0; i < flightsData.length; i++) {
          const flight = flightsData[i];
          const from = flight[0];
          const to = flight[1];

          const arcGenerator = new arc.GreatCircle(
            { x: from[1], y: from[0] },
            { x: to[1], y: to[0] }
          );

          const arcLine = arcGenerator.Arc(100, { offset: 10 });
          const features = [];
          arcLine.geometries.forEach((geometry) => {
            const line = new LineString(geometry.coords);
            line.transform('EPSG:4326', 'EPSG:3857');

            features.push(
              new Feature({
                geometry: line,
                finished: false,
              })
            );
          });

          this.addFlights(features, i * 50);
        }

        this.flightsLayer.on('postrender', this.animateFlights);
      });
  };

  addFlights = (features, timeout) => {
    window.setTimeout(() => {
      let start = Date.now();
      features.forEach((feature) => {
        feature.set('start', start);
        this.flightsSource.addFeature(feature);
        const duration = (feature.getGeometry().getCoordinates().length - 1) / 0.02;
        start += duration;
      });
    }, timeout);
  };

  animateFlights = (event) => {
    const vectorContext = getVectorContext(event);
    const frameState = event.frameState;
    vectorContext.setStyle(new Style({
      stroke: new Stroke({
        color: '#EAE911',
        width: 2,
      }),
    }));

    const features = this.flightsSource.getFeatures();
    for (let i = 0; i < features.length; i++) {
      const feature = features[i];
      const coords = feature.getGeometry().getCoordinates();
      const elapsedTime = frameState.time - feature.get('start');
      const elapsedPoints = elapsedTime * 0.02;

      if (elapsedPoints >= coords.length) {
        feature.set('finished', true);
      }

      if (!feature.get('finished')) {
        const maxIndex = Math.min(elapsedPoints, coords.length);
        const currentLine = new LineString(coords.slice(0, maxIndex));
        vectorContext.drawGeometry(currentLine);
      }
    }

    this.map.render();
  };

  render() {
    return (
      <div className="map-container">
        <div ref={this.mapRef} className="map" />
      </div>
    );
  }
}

export default MapComponent;
