import React, { Component } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import { bbox } from 'leaflet';
import '../LeafletMaps/Map2.css';

class MapComponent2 extends Component {
  constructor(props) {
    super(props);

    this.map = null;
    this.mapRef = React.createRef();
    this.state = {
      bbox: null,
    };
  }

  componentDidMount() {
    this.map = L.map(this.mapRef.current).setView([51.505, -0.09], 5);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data © <a href="https://openstreetmap.org">OpenStreetMap</a> contributors',
    }).addTo(this.map);

    const bounds = this.map.getBounds();
    L.rectangle(bounds, { color: 'red', weight: 1 }).addTo(this.map);

    const bboxString = bbox(bounds);
    this.setState({ bbox: bboxString });
  }

  componentWillUnmount() {
    this.map.remove();
    this.map = null;
  }

  render() {
    return (
      <div>
        <div ref={this.mapRef} style={{ height: '760px' }} />
        <p>Bbox: {this.state.bbox}</p>
      </div>
    );
  }
}

export default MapComponent2;
