import React, { Component } from 'react';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import 'leaflet-draw/dist/leaflet.draw.css';
import 'leaflet-draw';

class MapComponent2 extends Component {
  constructor(props) {
    super(props);

    this.map = null;
    this.mapRef = React.createRef();
  }

  componentDidMount() {
    this.map = L.map(this.mapRef.current).setView([51.505, -0.09], 5);

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data © <a href="https://openstreetmap.org">OpenStreetMap</a> contributors',
    }).addTo(this.map);

    const drawnItems = new L.FeatureGroup();
    this.map.addLayer(drawnItems);

    const drawControl = new L.Control.Draw({
      draw: {
        rectangle: true,
        polyline: false,
        circle: false,
        circlemarker: false,
        marker: false,
        polygon: false
      },
      edit: {
        featureGroup: drawnItems
      }
    });

    this.map.addControl(drawControl);

    this.map.on(L.Draw.Event.CREATED, (event) => {
      const { layer } = event;

      // Lakukan sesuatu dengan layer yang ditarik (misalnya, simpan koordinat atau tampilkan informasi)
      console.log('New layer:', layer);

      // Tambahkan layer ke drawnItems agar dapat ditampilkan pada peta
      drawnItems.addLayer(layer);
    });
  }

  componentWillUnmount() {
    this.map.remove();
    this.map = null;
  }

  render() {
    return <div ref={this.mapRef} style={{ height: '760px' }} />;
  }
}

export default MapComponent2;
